components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}

components/custom/product/product-view:
{{inject "bearerToken" settings.storefront_api.token}}
{{inject "showSwatch" theme_settings.show_card_swatch}}
{{#each product.reviews.messages}}
{{#if error}}
{{> components/common/alert-error error}}
{{/if}}
{{#if success}}
{{> components/common/alert-success success}}
{{/if}}
{{/each}}
<div class="productView" {{#if settings.data_tag_enabled}} data-event-type="product" data-entity-id="{{product.id}}"
    data-name="{{product.title}}"
    data-product-category="{{#each product.category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}"
    data-product-brand="{{product.brand.name}}"
    data-product-price="{{#if product.price.with_tax}}{{product.price.with_tax.value}}{{else}}{{product.price.without_tax.value}}{{/if}}"
    data-product-variant="single-product-option" {{/if}}>


    <section class="productView-images" data-image-gallery>
        {{!--
        Note that these image sizes are coupled to image sizes used in /assets/js/theme/common/product-details.js
        for variant/rule image replacement
        --}}

        <figure class="productView-image" data-image-gallery-main {{#if product.main_image}}
            data-zoom-image="{{getImageSrcset product.main_image (cdn theme_settings.default_image_product) 1x=theme_settings.zoom_size }}"
            {{/if}}>
            <div class="productView-img-container">
                {{!-- Remove the surrounding a-element if there is no main image. --}}
                {{#if product.main_image}}
                <a href="{{getImageSrcset product.main_image (cdn theme_settings.default_image_product) 1x=theme_settings.zoom_size}}"
                    target="_blank" {{#if schema}} itemprop="image" {{/if}}>
                    {{/if}}
                    {{> components/common/responsive-img
                    image=product.main_image
                    class="productView-image--default"
                    fallback_size=theme_settings.product_size
                    lazyload=theme_settings.lazyload_mode
                    default_image=theme_settings.default_image_product
                    otherAttributes="data-main-image"
                    }}
                    {{!-- Remove the surrounding a-element if there is no main image. --}}
                    {{#if product.main_image}}
                </a>
                {{/if}}
            </div>
        </figure>
        <ul class="productView-thumbnails">
            {{#each product.images}}
            {{#unless @index '>' 4}}
            <li class="productView-thumbnail">
                <a class="productView-thumbnail-link"
                    href="{{getImageSrcset this (cdn ../theme_settings.default_image_product) 1x=../theme_settings.zoom_size}}"
                    data-image-gallery-item
                    data-image-gallery-new-image-url="{{getImageSrcset this (cdn ../theme_settings.default_image_product) 1x=../theme_settings.product_size}}"
                    data-image-gallery-new-image-srcset="{{getImageSrcset this use_default_sizes=true}}"
                    data-image-gallery-zoom-image-url="{{getImageSrcset this (cdn ../theme_settings.default_image_product) 1x=../theme_settings.zoom_size}}">
                    {{> components/common/responsive-img
                    image=this
                    fallback_size=../theme_settings.productview_thumb_size
                    lazyload=../theme_settings.lazyload_mode
                    }}
                </a>
            </li>
            {{/unless}}
            {{/each}}
        </ul>
    </section>

    <section class="productView-details">
        <div class="productView-product">
            {{#if product.brand}}
            <h2 class="productView-brand">
                <a href="{{product.brand.url}}"><span>{{product.brand.name}}</span></a>
            </h2>
            {{/if}}
            <h1 class="productView-title">{{product.title}}</h1>

            <div class="yotpo bottomLine" data-yotpo-product-id="{{ product.id }}"></div>

            {{#if product.call_for_price}}
            <div class="call-for-price-label">
                {{product.call_for_price}}
            </div>
            {{/if}}

            <div class="price-rating {{#if product.price}}with-price {{else}}without-price{{/if}}">

                {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                <div class="productView-price show">
                    {{> components/products/price price=product.price}}
                </div>
                {{else}}
                <div class="productView-price show">
                    {{> components/elevate/login-for-pricing}}
                </div>
                {{/or}}



                <div class="productView-rating show">
                    {{#if settings.show_product_rating}}
                    {{> components/products/ratings rating=product.rating}}
                    <span class="productView-reviewLink product_review_count">
                        {{#if product.num_reviews '>' 0}}
                        <a href="{{product.url}}#product-reviews">
                            {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                        </a>
                        {{else}}
                        {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                        {{/if}}
                    </span>

                    {{#if settings.show_product_reviews}}
                    <span class="productView-reviewLink">
                        <a href="{{product.url}}{{#if is_ajax}}#write_review{{/if}}" {{#unless is_ajax
                            }}data-reveal-id="modal-review-form" {{/unless}} data-options='{"animation_speed": 0}'>
                            {{lang 'products.reviews.new'}}
                        </a>
                    </span>
                    {{> components/products/modals/writeReview}}
                    {{/if}}
                    {{/if}}
                </div>
            </div>
        </div>

        {{{region name="product_below_price"}}}

        <div class="productView-options">
            {{#if product.release_date }}
            <p>{{product.release_date}}</p>
            {{/if}}
            <form class="form" method="post" action="{{product.cart_url}}" enctype="multipart/form-data"
                data-cart-item-add>
                <input type="hidden" name="action" value="add">
                <input type="hidden" name="product_id" value="{{product.id}}" />
                <div data-product-option-change style="display:none;">
                    {{#each product.options}}
                    {{{dynamicComponent 'components/products/options'}}}
                    {{/each}}
                </div>

                {{#if product.id '==' '130'}}
                <div>
                    <p>Questions? Feel free to call for free project support. <a href="tel:5414501976">(541)
                            450-1976</a></p>
                </div>
                {{/if}}
                <div class="product-purchase-section">
                    {{#if product.can_purchase}}
                    {{> components/products/add-to-cart}}
                    {{/if}}
                </div>
                {{#if product.out_of_stock}}
                {{#if product.out_of_stock_message}}
                {{> components/common/alert-error product.out_of_stock_message}}
                {{else}}
                {{> components/common/alert-error (lang 'products.sold_out')}}
                {{/if}}
                {{/if}}
            </form>

            {{#if settings.show_wishlist}}
            {{> components/elevate/wishlist-dropdown}}
            {{/if}}
            {{#each product.custom_fields}}
                {{#if name '==' 'calculator'}}
            <div class="productView-options__calculatorButton">
                <a href="#!" id="calculator-modal-link">
                    <span>How Much Do I Need?</span>
                    <span class="icon-calculator show theme-icon"><img height="40" width="40" src="{{cdn 'img/calculator.png'}}" alt="calculator icon"></span>
                </a>
            </div>


                <dialog id="calculator-modal">
                    <button id="close-modal">&#x2715;</button>
                    {{#if value '==' 'surface'}}
                        {{> components/custom/epoxy-calculator }}
                    {{/if}}
                    {{#if value '==' 'volume'}}
                        {{> components/custom/deep-pour-calculator }}
                    {{/if}}
                </dialog>
                <script>
                    (function(){
                        const link = document.getElementById("calculator-modal-link")
                        const modal = document.getElementById("calculator-modal");
                        const close = document.getElementById("close-modal");

                        link.addEventListener("click", () => modal.showModal());
                        close.addEventListener("click", () => modal.close());
                    })()
                </script>
                {{/if}}
            {{/each}}

        </div>

        <div class="productView-shortDescription">
            {{{ region name="product_short_description" }}}
        </div>


        <div class="productView-infoIcons">
            {{#if product.warranty}}
            <a class="infoIcon tds" href="#technical-docs">
                <img width="100" height="100" src="{{cdn 'img/beaker.png'}}" alt="Technical Information">
                Technical Information
            </a>

            <a class="infoIcon sds" href="#technical-docs">
                <img width="100" height="100" src="{{cdn 'img/shield.png'}}" alt="Safety Data Sheet">
                Safety Data Sheet
            </a>
            {{/if}}

            {{#if product.videos}}
            <a class="infoIcon app-info" href="#videoSection">
                <img width="100" height="100" src="{{cdn 'img/gear.png'}}" alt="Application Information">
                Application Information
            </a>
            {{/if}}
        </div>

        {{product.detail_messages}}
        <div class="productView-info">
            <dl class="productView-info-dl" {{#unless product.sku}} style="display: none;" {{/unless}}>
                <dt class="productView-info-name sku-label" {{#unless product.sku}} style="display: none;" {{/unless}}>
                    {{lang 'products.sku'}}</dt>
                <dd class="productView-info-value" data-product-sku>{{product.sku}}</dd>
            </dl>

            {{#all product.weight theme_settings.show_product_weight}}
            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.weight'}}</dt>
                <dd class="productView-info-value" data-product-weight>{{product.weight}}</dd>
            </dl>
            {{/all}}

            {{#all product.width product.height product.depth theme_settings.show_product_dimensions}}
            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.width'}}</dt>
                <dd class="productView-info-value" data-product-width>
                    {{product.width}}
                    {{#if settings.measurements.length '==' 'Centimeters'}}
                    ({{lang 'products.measurement.metric'}})
                    {{else}}
                    ({{lang 'products.measurement.imperial'}})
                    {{/if}}
                </dd>
            </dl>

            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.height'}}</dt>
                <dd class="productView-info-value" data-product-height>
                    {{product.height}}
                    {{#if settings.measurements.length '==' 'Centimeters'}}
                    ({{lang 'products.measurement.metric'}})
                    {{else}}
                    ({{lang 'products.measurement.imperial'}})
                    {{/if}}
                </dd>
            </dl>

            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.depth'}}</dt>
                <dd class="productView-info-value" data-product-depth>
                    {{product.depth}}
                    {{#if settings.measurements.length '==' 'Centimeters'}}
                    ({{lang 'products.measurement.metric'}})
                    {{else}}
                    ({{lang 'products.measurement.imperial'}})
                    {{/if}}
                </dd>
            </dl>
            {{/all}}
            {{#if product.min_purchase_quantity}}
            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.min_purchase_quantity'}}</dt>
                <dd class="productView-info-value">{{lang 'products.purchase_units'
                    quantity=product.min_purchase_quantity}}</dd>
            </dl>
            {{/if}}
            {{#if product.max_purchase_quantity}}
            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.max_purchase_quantity'}}</dt>
                <dd class="productView-info-value">{{lang 'products.purchase_units'
                    quantity=product.max_purchase_quantity}}</dd>
            </dl>
            {{/if}}
            {{#if product.gift_wrapping_available}}
            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.gift_wrapping'}}</dt>
                <dd class="productView-info-value">{{lang 'products.gift_wrapping_available'}}</dd>
            </dl>
            {{/if}}
            {{#if product.shipping}}
            {{#if product.shipping.calculated}}
            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                <dd class="productView-info-value">{{lang 'products.shipping_calculated'}}</dd>
            </dl>
            {{else}}
            {{#if product.shipping.price.value '===' 0}}
            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                <dd class="productView-info-value">{{lang 'products.shipping_free'}}</dd>
            </dl>
            {{else}}
            <dl class="productView-info-dl">
                <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                <dd class="productView-info-value">{{lang 'products.shipping_fixed'
                    amount=product.shipping.price.formatted}}</dd>
            </dl>
            {{/if}}
            {{/if}}
            {{/if}}

            {{#if settings.bulk_discount_enabled}}
            <div class="productView-info-bulkPricing">
                {{> components/products/bulk-discount-rates bulk_discount_rates=product.bulk_discount_rates}}
            </div>
            {{/if}}

            {{#if theme_settings.show_custom_fields_tabs '!==' true}}
            <dl class="productView-info-dl">
                {{#each product.custom_fields}}
                <dt class="productView-info-name">{{name}}:</dt>
                <dd class="productView-info-value">{{{value}}}</dd>
                {{/each}}
            </dl>
            {{/if}}
        </div>

        <div class="share-links">
            {{> components/common/share url=product.url}}
        </div>
    </section>
</div>


<!-- usp section start -->
<div class="product-usp">
    {{> components/elevate/usp-section}}
</div>
<!-- usp section end -->

{{{region name="product_above_content"}}}



<div id="previewModal" class="modal modal--large" data-reveal data-options='{"animation_speed": 0}'>
    <a href="#" class="modal-close" aria-label="{{lang 'common.close'}}" role="button">
        <span aria-hidden="true">&#215;</span>
    </a>
    <div class="modal-content"></div>
    <div class="loadingOverlay"></div>
</div>

components/elevate/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<ul class="navUser-section show">
    <li class="navUser-item navUser-item-currency">
        <a class="navUser-action navUser-action--currencySelector has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">
            {{lang 'common.currency' code=currency_selector.active_currency_code}} 
            <i class="icon navPages-action-moreIcon" aria-hidden="true">
                <span class="icon-multimedia theme-icon" data-icon="&#xe902;"></span>
            </i>
        </a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>
                    {{else}}
                    {{name}}
                    {{/if}}
                </a>
            </li> 
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/elevate/footer:
{{#unless theme_settings.hide_footer_logo}} 
<div class="footer-logo">
    {{#if page_type '===' 'default'}}
        <div class="header-logo {{#if settings.store_logo.image}}logo-image{{else}}logo-text{{/if}}">
            {{> components/common/store-logo}}
        </div>
    {{else}} 
        <div class="header-logo {{#if settings.store_logo.image}}logo-image{{else}}logo-text{{/if}}">
            {{> components/common/store-logo}}
        </div>
    {{/if}}
</div>
{{/unless}} 

