components/elevate/instagram:
{{#unless theme_settings.hide_instagram}}
<div class="instagram">
    <div class="section-title">
        <h2 class="section-heading"> 
            <span class="icon-instagram show theme-icon" data-icon="&#xe905;"></span>
            <a href="{{theme_settings.instagram-link}}">{{theme_settings.instagram-title}}</a>
        </h2>
        {{#if theme_settings.instagram-sub}}
            <p class="section-sub-heading">
                {{theme_settings.instagram-sub}}
            </p>
        {{/if}}
        <div class="theme-social-icons">
            {{> components/elevate/social-links}}
        </div>
    </div>
    {{#if theme_settings.elfsight_instagram_app_code}}
    <div class="instagram-section">
		<script src="https://apps.elfsight.com/p/platform.js" defer></script>
		<div class="{{theme_settings.elfsight_instagram_app_code}}"></div>
 	</div>
 	{{/if}}
</div>
{{/unless}}

components/elevate/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}
